import styled from "styled-components";

export const Container = styled.div`
  display: flex;
  flex: 1;
  align-items: center;
  width: 100%;
  height: 4rem;
  background: #000;
`;

export const Logo = styled.div`
  width: 2rem;
  height: 1rem;
  margin-left: 2rem;
  background: red;
  border-radius: 10px;
`;
